import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./signup.css";
import Chatbot from "../components/chatbot";
import styled from "styled-components";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { auth, googleProvider, facebookProvider } from "../Frontfirebase";
import NavbarPage from "../components/navnew";
const Signup = () => {
  let history = useHistory();

  // ที่เก็บ state
  const [imagesProfile, setImagesProfile] = useState("/img/profile.png"); //สร้าง State เพื่อเก็บรูปโปรไฟล์
  const [username, setUsername] = useState("");
  const [firstname, setFirstname] = useState("");
  const [surname, setSurname] = useState("");
  const [sex, setSex] = useState("");
  const [date, setDate] = useState();
  const [phone, setPhone] = useState("");
  const [province, setProvince] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailis_inVaild, setEmailis_inVaild] = useState(false);




  // ฟังกชันการเลือกเพศใน input

  const selectSex = (e) => {
    if (e.target.value === "male") {
      setSex(e.target.value);
    } else setSex(e.target.value);
  };
  const ProfileChange = (event) => {  

    event.preventDefault(); // ใส่ไว้ไม่ให้ refresh หน้าเว็บ
    let files = event.target.files; //ใช้เพื่อแสดงไฟลทั้งหมดที่กดเลือกไฟล
    Setphoto(files[0])
    Seterror()
    let reader = new FileReader(); //ใช้ Class  FileReader เป็นตัวอ่านไฟล์
    reader.readAsDataURL(files[0]); //เป็นคำสั่งสำหรับการแปลง url มาเป็น file
    reader.onloadend = () => {
      setImagesProfile(reader.result); // ใส่ข้อมูลเข้าไปยัง state ผาน setImagesProfile
    };

  };

  // Style มาตรฐานของ Formik

  const styles = {
    row: {
      marginTop: "8rem",
    },
    txt1: {
      fontFamily: "Roboto",
      fontSize: "2.2rem",
      color: "#fff",
      marginBottom: "1rem",
      fontWeight: "700",
      textAlign: "center",
    },
    txt2: {
      fontFamily: "Roboto",
      fontSize: "1rem",
      color: "#fff",
    },
  };

  //object schema สำหรับทำ validation

  const RegisterSchema = Yup.object().shape({
    name: Yup.string()
      .min(2, "สั้นเกินไป")
      .max(50, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      .test("", "กรุณาใส่ตัวอักษรเท่านั้น", (value) => {
        return !/^[0-9]+$/.test(value);
      }),
    lastname: Yup.string()
      .min(2, "สั้นเกินไป")
      .max(50, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      .test("", "กรุณาใส่ตัวอักษรเท่านั้น", (value) => {
        return !/^[0-9]+$/.test(value);
      }),
    username: Yup.string()
      .min(2, "สั้นเกินไป")
      .max(50, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้"),
    phone: Yup.number()
      .max(10000000000, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      .typeError("กรุณาใส่ตัวเลขเท่านั้น"),
    email: Yup.string()
      .email("รูปแบบอีเมลไม่ถูกต้อง")
      .required("จำเป็นต้องกรอกช่องนี้"),
    password: Yup.string()
      .min(6, "กรุณากรอกตัวอักษรอย่างน้อย 6 ตัว")
      .max(20, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้"),
    confirmPassword: Yup.string()
      .min(6, "กรุณากรอกตัวอักษรอย่างน้อย 6 ตัว")
      .max(20, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      //check is password match ?
      .test("รหัสผ่านตรงกัน", "รหัสผ่านไม่ตรงกัน", function (value) {
        return this.parent.password === value;
      }),
  });

  return (
    <div>
      <NavbarPage />
      <div className="container-signup">
        <form className="LoginForm">
          <p className="h2 text-center mb-2 font-weight-bold text1-signup">สมัครสมาชิก</p>

          <div className="profile-badformpost-img">
            <img className="img-circle" src={imagesProfile} />
            <div className="rank-label-container">
              <span className="label label-default rank-label">
                <div className="formpost-ImageUpload">
                  <label htmlFor="FileInput">
                    <div className="fileinput">
                      <img className="uploadiconprofile" src="/img/edit.png" />
                    </div>
                  </label>
                  <div className="buttoninputprofile">
                    <input
                      className="uploadinputprofile"
                      id="FileInput"
                      type="file"
                      onChange={ProfileChange}
                      multiple
                      accept="image/png, image/jpeg , image/jpg"
                    />
                  </div>
                </div>
              </span>
            </div>
          </div> 

        </form>
      </div>
      <Chatbot />
    </div>
  );
};

export default Signup;
