import React, { useEffect, useState, useContext } from "react";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";
import "./commentitem.css";
import usercontext from "../context/usercontext";



const Commentitem = ({ data, ok ,uid , commmentid , postid}) => {

  const [isActive, setIsActive] = useState(false);
  const onClick = () => setIsActive(!isActive);
  let { user, setUser } = useContext(usercontext);
  const [allcomment , Setallcomment] = useState()
  const [item , Setitem] = useState([])
  const [checkedittext , Setcheckedittext] = useState(false)
  // const [edittextcomment , Setedittextcomment] = useState()
  const [texteditcomment , Settexteditcomment] = useState()
  const [textcomment , Settextcomment] = useState()

 

  const deleted = async (commentid) => {
      const postdelete = await Axios.post(`http://localhost:7000/post/delete/comment/${commentid}`);
      // const getcomment = await Axios.get(`http://localhost:7000/post/comment/${uid}`)
      // console.log( getcomment.data.item);
      // Setallcomment( getcomment.data.item);
      window.location.reload(false);
  };
  const edit = async () =>{
    Setcheckedittext(true)
  }
  const handleedit = async (commentid) =>{
  const editcomment = await Axios.post(`http://localhost:7000/post/edit/comment/${commentid}`,  {textcomment} )
  
  }

  const handlecomment = async (e) =>{
    try{
      e.preventDefault()
      let sentdata = {textcomment , username : data[0].username , userid : user.uid}
      
      const sentcomment = await Axios.post(`http://localhost:7000/post/comment/${postid}`, sentdata)
      // const getcomment = await Axios.get(`http://localhost:7000/post/comment/${uid}`)
      // Setallcomment(getcomment.data.item)
      
    }catch(err){
      console.log(err)
    }
  }

  const gg = async () => {
    try {
      Setedittextcomment(data.textcomment)
      
      // const getcomment = await Axios.get(`http://localhost:7000/post/comment/${uid}`)
      // Setallcomment(getcomment.data.item)

      // const getcomment = await Axios.get(`http://localhost:7000/post/commentnew/${commmentid}`)
      // Setallcomment(getcomment.data.item)
   
      
    } catch (err) {
      console.log("error");
    }
  };
  useEffect(() => {
    gg();
  }, []);
  
  return (
    <div>
      {allcomment ? allcomment.map(ok =>{
        return (<div>
            <div className="row mypostcommentrow">
      <div className="column1 mypostcommentrow1">
        <div class="vl"></div>
        <div className="mypost-comment-img1">
          <img className="img-circle1" src="/img/profile.png" />
          <div className="mypost-comment-name1">
           {data ? "@" : null}{data.username}
            <span className="mypost-comment-time1"> {data.datetime} </span>
          </div>
          <br />
         {checkedittext ? <div><input value={texteditcomment} onChange={(e) =>{Settexteditcomment(e.target.value)}}></input> <button onClick={() => handleedit(data.commentid)}>ตกลง</button> </div>: <div className="mypost-comment-comments1">
            <div className="mypostcomment1">{texteditcomment}</div> 
          </div>  
         } 
        </div> 
       </div> 
       {user && data.userid == user.uid ? <div className="column2 mypostcommentrow2">
        <div className="menu-containermypostcommentsetting">
          <div onClick={onClick} className="mypostcommentbuttonsetting">
            <img 
              className="mypostcommentimg-setting"
              src="/img/setting.png"
              alt="avatar"
            ></img> 
       </div>

          <div
            className={`mypostcommentmenusetting ${
              isActive ? "active" : "inactive"
            }`}
          >
            <ul className="ul-mypostcommentmenusetting">
           <li className="li-mypostcommentmenusetting">
                <a className="a-mypostcommentmenusetting"
                onClick={() => edit(data.commentid)}
                >
                    แก้ไขคอมเมนต์
                </a>
              </li> 
              <li className="li-mypostcommentmenusetting">
                <a
                  className="a-mypostcommentmenusetting"
                  onClick={() => deleted(data.commentid)}
                >
                  {" "}
                  ลบคอมเมนต์{" "}
                </a>
              </li> 
            </ul>
          </div>
        </div>
      </div> : null }
    </div> 
        </div>)
      }) : null}
      <Form>
        <div className="row post-comment-comments1">
                <div className="post-profilecomment-img1">
                 
                  <img className="img-circle" src="/img/profile.png" />
                </div>
                <div className="row post-comment-commentsall">
                  <div
                    className="post-writecommemt col-lg-6 col-10"
                    controlId="exampleForm.ControlTextarea1"
                  >
                  
                    <input className="inputcomment" placeholder="เขียนความคิดเห็น..." value={textcomment} onChange={(e) =>{Settextcomment(e.target.value)}}/>
                  </div>

                  <div>
                    <div className="postbuttonsend">
                          <button className="mypostbuttonsends" type="submit">
                                <i className="fa fa-paper-plane"></i>
                              </button>
                    </div>
                  </div>
                  
                </div>
                
              </div>
              </Form>
    </div>
    
  );
};

export default Commentitem;
