import axios from "axios";
import React, { useEffect, useState } from "react";
import NavbarPage from "../components/navnew";
import Axios from "axios";
import "./post.css";
import { Link, useHistory } from "react-router-dom";
import Chatbot from "../components/chatbot";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import {
  auth,
  googleProvider,
  facebookProvider,
  firestore,
} from "../Frontfirebase";
const Post = () => {
  const [data, Setdata] = useState();
  const [show, Setshow] = useState();
  const history = useHistory();

  const [isActive, setIsActive] = useState(false);
  const onClick = () => setIsActive(!isActive);

  const ok = async () => {
    const showdata = await firestore
      .collection("Post")
      .onSnapshot((querySnapshot) => {
        let item = [];
        querySnapshot.forEach((doc) => {
          item.push(doc.data());
          console.log(item);
        });
        Setshow(item);
      });
  };

  useEffect(() => {
    ok();
  }, []);

  const deleted = async (uid) => {
    const postdelete = await Axios.post(
      `http://localhost:7000/post/delete/${uid}`
    );
    console.log(postdelete.data);
  };

  return (
    <div>
      <NavbarPage />
      {/* <h1 className="h1-post">โพสต์ทั้งหมด</h1> */}
      <div className="container-post1">
        <div className="row postrow">
          <div className="column1-postrow1">
            <div className="post-img">
              <img className="monkey" src="/img/logo v3.png" />
            </div>
          </div>  
          <div className="column2-postrow2">
            <div className="post-linkformpost1">
                แจ้งข้อมูลคนโกงได้ที่นี่เลย
            </div>
            <br />
            <div className="post-linkformpost2">
              <Link to={`/linkruleshow/`}>
                <button className="buttonpost" type="submit">
                  คลิก
                </button>
              </Link>
            </div>
          </div>  
        </div>
      </div>
      <div>
        <div className="container-mypost">
          <div className="cotainer-mypost2">
            <div className="mypost-profile-img">
              {/* {ok.file ? <img className="img-circle" src={`/uploads/${ok.file[0].filename}`}  /> : <img className="img-circle" src="/img/profile.png" /> } */}
              <img className="img-circle" src="/img/profile.png" />
              <div className="mypost-name">
                {data ? data[0].username : null}
              </div>
              <br />
              <div className="mypost-date">
                {ok.date}
                {/* <span className="mypost-time">23:38 </span> */}
              </div>
            </div>

            {/* <div className="mypostbuttonshared">
              <a className="mypostbuttonshare" href="/post/edit">
                <i class="fa fa-share"></i>
              </a>
            </div> */}

            <div className="container-mypostsetiing">
              <div className="menu-containermypostsetting">
                <div onClick={onClick} className="mypostbuttonsetting">
                  <img
                    className="mypostimg-setting"
                    src="/img/setting.png"
                    alt="avatar"
                  ></img>
                </div>
                <div
                  className={`mypostmenusetting ${
                    isActive ? "active" : "inactive"
                  }`}
                >
                  <ul className="ul-mypostmenusetting">
                    <li className="li-mypostmenusetting">
                      <a className="a-mypostmenusetting">
                        <Link
                          className="a-mypostmenusetting1"
                          to={`/post/edit/${ok.uid}`}
                        >
                          แก้ไขโพสต์
                        </Link>
                      </a>
                    </li>
                    <li className="li-mypostmenusetting">
                      <a
                        className="a-mypostmenusetting"
                        onClick={() => deleted(ok.uid)}
                      >
                        {" "}
                        ลบโพสต์{" "}
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            </div>

            <div className="container-mypost3">
              <div className="mypostprofile-bad-img">
                {ok.resultfileitem ? (
                  <img
                    className="img-circle"
                    src={`${ok.resultfileitem.url}`}
                  />
                ) : (
                  <img className="img-circle" src="/img/profile.png" />
                )}
              </div>
              <Form className="formsize-mypost">
                <Form.Row>
                  <Form.Group
                    as={Col}
                    className="mypost-left col-lg-6 col-sm-6 col-12"
                    controlId="formGridName"
                  >
                    <Form.Label className="text-mypost">
                      ชื่อ (ผู้โกง){" "}
                      <span className="spanmypost">{ok.name}</span>
                    </Form.Label>
                  </Form.Group>

                  <Form.Group as={Col} controlId="formGridLastname">
                    <Form.Label className="text-mypost">
                      นามสกุล (ผู้โกง){" "}
                      <span className="spanmypost">{ok.surname}</span>
                    </Form.Label>
                  </Form.Group>
                </Form.Row>

                <Form.Row>
                  <Form.Group
                    as={Col}
                    className="mypost-left col-lg-6 col-sm-6 col-12"
                    controlId="formGridId"
                  >
                    <Form.Label className="text-mypost">
                      เลขบัตรประชาชน (ผู้โกง){" "}
                      <span className="spanmypost">{ok.id}</span>
                    </Form.Label>
                  </Form.Group>

                  <Form.Group
                    as={Col}
                    controlId="formGridAccountnumber"
                  >
                    <Form.Label className="text-mypost">
                      เลขที่บัญชี (ผู้โกง){" "}
                      <span className="spanmypost">
                        {ok.accountnumber}
                      </span>
                    </Form.Label>
                  </Form.Group>
                </Form.Row>

                <Form.Row>
                  <Form.Group
                    as={Col}
                    className="mypost-left col-lg-6 col-sm-6 col-12"
                    controlId="formGridNameproduct"
                  >
                    <Form.Label className="text-mypost">
                      ชื่อสินค้า{" "}
                      <span className="spanmypost">
                        {ok.nameproduct}
                      </span>
                    </Form.Label>
                  </Form.Group>

                  <Form.Group as={Col} controlId="formGridCategory">
                    <Form.Label className="text-mypost">
                      หมวดหมู่สินค้า{" "}
                      <span className="spanmypost">
                        {ok.productcategory}
                      </span>
                    </Form.Label>
                  </Form.Group>
                </Form.Row>

                <Form.Row>
                  <Form.Group
                    as={Col}
                    className="mypost-left col-lg-6 col-sm-6 col-12"
                    controlId="formGridPrice"
                  >
                    <Form.Label className="text-mypost">
                      จำนวนเงิน (บาท){" "}
                      <span className="spanmypost">{ok.money}</span>
                    </Form.Label>
                  </Form.Group>

                  <Form.Group as={Col} controlId="formGridCategory">
                    <Form.Label className="text-mypost">
                      ธนาคาร{" "}
                      <span className="spanmypost">{ok.bank}</span>
                    </Form.Label>
                  </Form.Group>
                </Form.Row>

                <Form.Row>
                  <Form.Group
                    as={Col}
                    className="mypost-left col-lg-6 col-sm-6 col-12"
                    controlId="formGridDate"
                  >
                    <Form.Label className="text-mypost">
                      วันที่โดนโกง{" "}
                      <span className="spanmypost">{ok.datetime}</span>
                    </Form.Label>
                  </Form.Group>

                  <Form.Group as={Col} controlId="formGridSocial">
                    <Form.Label className="text-mypost">
                      ช่องทางที่โดนโกง{" "}
                      <span className="spanmypost">{ok.social}</span>
                    </Form.Label>
                  </Form.Group>
                </Form.Row>

                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label className="text-mypost">
                    รายละเอียดเพิ่มเติม{" "}
                    <span className="spanmypost">{ok.other}</span>
                  </Form.Label>
                </Form.Group>
                <div className="img-holder-badslip">
                  {ok.item
                    ? ok.item.map((res) => {
                        return (
                          <img
                            className="img-bad"
                            alt=""
                            src={`${res.url}`}
                            style={{ overflow: "hidden" }}
                            onMouseOver={(e) =>
                              (e.currentTarget.style = {
                                transform: "scale(1.25)",
                                overflow: "hidden",
                              })
                            }
                            onMouseOut={(e) =>
                              (e.currentTarget.style = {
                                transform: "scale(1)",
                                overflow: "hidden",
                              })
                            }
                          />
                        );
                      })
                    : null}
                </div>
              </Form>
              <div className="line-comment1"></div>
              <div className="container-mypost4">
                {Democomments ? (
                  Democomments.map((value, index) => {
                    return (
                      <Commentitem data={value} ok={ok} key={index} />
                    );
                  })
                ) : (
                  <div>
                  </div>
                )}

                {/* <div className="line-comment2"></div> */}
              </div>
              <h2 className="commentother">ดูอีก 3 ความคิดเห็น</h2>
              <div className="row mypost-comment-comments2">
                <div className="mypost-profilecomment-img">
                  {/* {ok.file ? <img className="img-circle" src={`/uploads/${ok.file[0].filename}`}  /> : <img className="img-circle" src="/img/profile.png" /> } */}
                  <img className="img-circle" src="/img/profile.png" />
                </div>
                <div className="row mypost-comment-commentsall">
                  <div
                    className="mypost-writecommemt col-lg-6 col-10"
                    controlId="exampleForm.ControlTextarea1"
                  >
                    <input className="inputcomment" placeholder="เขียนความคิดเห็น..." />
                  </div>

                  <div>
                    <div className="column2 mypostbuttonsend">
                      <a className="mypostbuttonsends" href="">
                        <i className="fa fa-paper-plane"></i>
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>







      {/* {show ? show .map(ok =>{
        return (
        <div>
        {ok.name} <br></br>
        {ok.accountnumber} <br></br>
        {ok.nameproduct} <br></br>
        {ok.money} <br></br>
        {ok.datetime} <br></br>
        <Link to={`/post/edit/${ok.uid}`}> Edit </Link>
        <button onClick={() =>  deleted(ok.uid)}> x </button> 
        </div>
      )
      }) : null} */}

      <Chatbot />
    </div>
  );
};

export default Post;
