import React, { useEffect, useState } from "react";
import Navbar from "../components/navbar";
import "./forgetpass.css";
import Chatbot from "../components/chatbot";
// import { Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import styled from 'styled-components';
import { Formik, Form, Field, ErrorMessage, FastField } from 'formik'
import * as Yup from 'yup'
import {
  auth,
  googleProvider,
  facebookProvider,
} from "../Frontfirebase";
// import { MDBInput } from "mdbreact";
import axios from "axios";


const Forgetpass = () => {
  
  let history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailis_inVaild, setEmailis_inVaild] = useState(false);

  const LoginSubmit = (e) => {
    e.preventDefault()
 auth.signInWithEmailAndPassword(email, password)
      .then((result) => {
        console.log(result);
        history.push("/");
      })
      .catch(() => {
        setEmailis_inVaild(true)
      });
  };

  const googleLogin = async (e) => {
    e.preventDefault();
    const result = await auth.signInWithPopup(googleProvider);
    console.log(result)
    axios.post("http://localhost:7000/user/googlesignup", { result: result })
      .then((result) => {
        console.log(result.data);
        history.push("/");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const facebookLogin = async (e) => {
    e.preventDefault();
    const result = await auth.signInWithPopup(facebookProvider);
    console.log(result)
    axios.post("http://localhost:7000/user/facebooksignup", { result: result })
      .then((result) => {
        console.log(result.data);
        history.push("/");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const styles = {
    row: {
        marginTop: '8rem'
    },
    txt1: {
        fontFamily: 'Roboto',
        fontSize: '2.2rem',
        color: '#fff',
        marginBottom: '1rem',
        fontWeight: '700',
        textAlign: 'center'
    },
    txt2: {
        fontFamily: 'Roboto',
        fontSize: '1rem',
        color: '#fff'
    }
}

  const RegisterSchema = Yup.object().shape(
    {
      email: Yup.string()
          .email('รูปแบบอีเมลไม่ถูกต้อง')
          .required('จำเป็นต้องกรอกช่องนี้'),
      password: Yup.string()
          .min(6, 'กรุณากรอกตัวอักษรอย่างน้อย 6 ตัว')
          .max(20, 'ยาวเกินไป')
          .required('จำเป็นต้องกรอกช่องนี้')
    }
);
console.log(emailis_inVaild)


  return (
    <div>
      <Navbar />
      <h1>Forgetpass</h1>
    </div>
  );
};

export default Forgetpass;
