import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./profile.css";
import Chatbot from "../components/chatbot";
import styled from "styled-components";
import axios from "axios";
import { auth, googleProvider, facebookProvider } from "../Frontfirebase";
import NavbarPage from "../components/navnew";
import { MDBInputGroup } from "mdbreact";
const Signup = () => {
  let history = useHistory();

  // ที่เก็บ state
  const [imagesProfile, setImagesProfile] = useState("/img/profile.png"); //สร้าง State เพื่อเก็บรูปโปรไฟล์
  const [username, setUsername] = useState("");
  const [firstname, setFirstname] = useState("");
  const [surname, setSurname] = useState("");
  const [sex, setSex] = useState("");
  const [date, setDate] = useState();
  const [phone, setPhone] = useState("");
  const [province, setProvince] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailis_inVaild, setEmailis_inVaild] = useState(false);
  const [photo, Setphoto] = useState("");
  const [error, Seterror] = useState();


  // ฟังกชันการเลือกเพศใน input
  const selectSex = (e) => {
    if (e.target.value === "male") {
      setSex(e.target.value);
    } else setSex(e.target.value);
  };
  const ProfileChange = (event) => {  

    event.preventDefault(); // ใส่ไว้ไม่ให้ refresh หน้าเว็บ
    let files = event.target.files; //ใช้เพื่อแสดงไฟลทั้งหมดที่กดเลือกไฟล
    Setphoto(files[0])
    Seterror()
    let reader = new FileReader(); //ใช้ Class  FileReader เป็นตัวอ่านไฟล์
    reader.readAsDataURL(files[0]); //เป็นคำสั่งสำหรับการแปลง url มาเป็น file
    reader.onloadend = () => {
      setImagesProfile(reader.result); // ใส่ข้อมูลเข้าไปยัง state ผาน setImagesProfile
    };

  };


  return (
    <div>
      <NavbarPage />
      <div className="container-signup">
        <form className="LoginForm">
          <p className="h2 text-center mb-2 font-weight-bold text1-signup">แก้ไขข้อมูลส่วนตัว</p>

          <div className="profile-badformpost-img">
            <img className="img-circle" src={imagesProfile}/>
            <div className="rank-label-container">
              <span className="label label-default rank-label">
                <div className="formpost-ImageUpload">
                  <label htmlFor="FileInput">
                    <div className="fileinput">
                      <img className="uploadiconprofile" src="/img/edit.png" />
                    </div>
                  </label>
                  <div className="buttoninputprofile">
                    <input
                      className="uploadinputprofile"
                      id="FileInput"
                      type="file"
                      onChange={ProfileChange}
                      multiple
                      accept="image/png, image/jpeg , image/jpg"
                    />
                  </div>
                </div>
              </span>
            </div>
          </div> 

          <div className="col-md-12">

          <div className="form-group my-0">
            <label className="label-form-title-profile">Username</label>
            <div className="form-inside-profile">
              <MDBInputGroup
                containerClassName="mt-0"
                size="sm"
              />
            </div>
          </div>

          <div className="form-group my-0">
            <label className="label-form-title-profile">ชื่อจริง</label>
            <div className="form-inside-profile">
              <MDBInputGroup
                  material
                  containerClassName="mt-0"
                  size="sm"
                />
            </div>
          </div>

          <div className="form-group my-0">
            <label className="label-form-title-profile">นามสกุล</label>
            <div className="form-inside-profile">
                <p>เหลือจันทร์</p>
            </div>
          </div>

          <div className="form-group my-0">
            <label className="label-form-title-profile">เพศ</label>
            <div className="form-inside-profile">
                <p>ชาย</p>
            </div>
          </div>

          <div className="form-group my-0">
            <label className="label-form-title-profile">วันเกิด</label>
            <div className="form-inside-profile">
                <p><span>09</span> / <span>02</span> / <span>2000</span></p>
            </div>
          </div>

          <div className="form-group my-0">
            <label className="label-form-title-profile">เบอร์โทรศัพท์</label>
            <div className="form-inside-profile">
                <p>0930239802</p>
            </div>
          </div>

          <div className="form-group my-0">
            <label className="label-form-title-profile">จังหวัด</label>
            <div className="form-inside-profile">
                <p>กรุงเทพมหานคร</p>
            </div>
          </div>

            <div className="col-md-12 mt-2">
              <button className="btn-block LoginFacebook">
                <div>
                  <i class="fas fa-user-edit"></i>
                </div>
                <p className="mx-auto my-1">แก้ไขข้อมูลส่วนตัว</p>
              </button>
              <button className="btn-block LoginGoogle">
                <div>
                  <i class="fas fa-unlock"></i>
                </div>
                <p className="mx-auto my-1">เปลี่ยนรหัสผ่าน</p>
              </button>
            </div>            

          </div>

        </form>
      </div>
      <Chatbot />
    </div>
  );
};

export default Signup;
