import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./signup.css";
import Chatbot from "../components/chatbot";
import styled from "styled-components";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { auth, googleProvider, facebookProvider } from "../Frontfirebase";
import NavbarPage from "../components/navnew";

const Changepass = () => {
  let history = useHistory();

  // ที่เก็บ state
  const [username, setUsername] = useState("");
  const [firstname, setFirstname] = useState("");
  const [surname, setSurname] = useState("");
  const [sex, setSex] = useState("");
  const [date, setDate] = useState();
  const [phone, setPhone] = useState("");
  const [province, setProvince] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailis_inVaild, setEmailis_inVaild] = useState(false);

  // ฟังกชันการ Signup
  const SignupSubmit = (e) => {
    e.preventDefault();
    console.log("submit");
    axios
      .post("http://localhost:7000/user/signup", {
        username: username,
        firstname: firstname,
        surname: surname,
        sex: sex,
        date: date,
        phone: phone,
        province: province,
        email: email,
        password: password,
      })
      .then((result) => {
        console.log("signup success", result);
        auth
          .signInWithEmailAndPassword(email, password)
          .then((result) => {
            console.log(result);
            history.push("/");
          })
          .catch((err) => {
            console.log(err);
          });
        history.push("/");
      })
      .catch((err) => {
        setEmailis_inVaild(true);
      });
  };

  // ฟังกชันการเลือกเพศใน input
  const selectSex = (e) => {
    if (e.target.value === "male") {
      setSex(e.target.value);
    } else setSex(e.target.value);
  };

  // Style มาตรฐานของ Formik
  const styles = {
    row: {
      marginTop: "8rem",
    },
    txt1: {
      fontFamily: "Roboto",
      fontSize: "2.2rem",
      color: "#fff",
      marginBottom: "1rem",
      fontWeight: "700",
      textAlign: "center",
    },
    txt2: {
      fontFamily: "Roboto",
      fontSize: "1rem",
      color: "#fff",
    },
  };

  //object schema สำหรับทำ validation
  const RegisterSchema = Yup.object().shape({
    name: Yup.string()
      .min(2, "สั้นเกินไป")
      .max(50, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      .test("", "กรุณาใส่ตัวอักษรเท่านั้น", (value) => {
        return !/^[0-9]+$/.test(value);
      }),
    lastname: Yup.string()
      .min(2, "สั้นเกินไป")
      .max(50, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      .test("", "กรุณาใส่ตัวอักษรเท่านั้น", (value) => {
        return !/^[0-9]+$/.test(value);
      }),
    username: Yup.string()
      .min(2, "สั้นเกินไป")
      .max(50, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้"),
    phone: Yup.number()
      .max(10000000000, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      .typeError("กรุณาใส่ตัวเลขเท่านั้น"),
    email: Yup.string()
      .email("รูปแบบอีเมลไม่ถูกต้อง")
      .required("จำเป็นต้องกรอกช่องนี้"),
    password: Yup.string()
      .min(6, "กรุณากรอกตัวอักษรอย่างน้อย 6 ตัว")
      .max(20, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้"),
    confirmPassword: Yup.string()
      .min(6, "กรุณากรอกตัวอักษรอย่างน้อย 6 ตัว")
      .max(20, "ยาวเกินไป")
      .required("จำเป็นต้องกรอกช่องนี้")
      //check is password match ?
      .test("รหัสผ่านตรงกัน", "รหัสผ่านไม่ตรงกัน", function (value) {
        return this.parent.password === value;
      }),
  });

  return (
    <div>
      <NavbarPage />
      <div className="container-signup">
        <form className="LoginForm">
          <img src="/img/logoLogin.png" className="Logo-signup" />
          <p className="h2 text-center mb-2 font-weight-bold text1-signup">เปลี่ยนรหัสผ่าน</p>

          <div className="col-md-12">
            <Formik
              initialValues={{
                name: "",
                lastname: "",
                email: "",
                password: "",
                confirmPassword: "",
                phone: "",
                username: "",
              }}
              validationSchema={RegisterSchema}
              onSubmit={(values) => {
                // same shape as initial values
                console.log(values);
              }}
            >
              {({ errors, touched }) => (
                <Form>
                  <div className="form-group mb-1">
                    <label htmlFor="password" style={styles.txt2}>
                      Password
                    </label>
                    <Field
                      name="password"
                      type="password"
                      className={`form-control ${
                        touched.password
                          ? errors.password
                            ? "is-invalid"
                            : "is-valid"
                          : ""
                      }`}
                      id="password"
                      placeholder="Password"
                      onKeyUp={(e) => {
                        setPassword(e.target.value);
                      }}
                    />
                    <ErrorMessage
                      component="div"
                      name="password"
                      className="invalid-feedback"
                    />
                  </div>

                  <div className="form-group mb-1">
                    <label htmlFor="confirmPassword" style={styles.txt2}>
                      Confirm Password
                    </label>
                    <Field
                      name="confirmPassword"
                      type="password"
                      className={`form-control ${
                        touched.confirmPassword
                          ? errors.confirmPassword
                            ? "is-invalid"
                            : "is-valid"
                          : ""
                      }`}
                      id="confirmPassword"
                      placeholder="Confirm Password"
                    />
                    <ErrorMessage
                      component="div"
                      name="confirmPassword"
                      className="invalid-feedback"
                    />
                  </div>

                </Form>
              )}
            </Formik>

            <button className="btn-block LoginFacebook mt-5">
              <div>
                <i class="fas fa-save pr-1"></i>
              </div>
              <p className="mx-auto my-1">บันทึกข้อมูล</p>
            </button>

          </div>
        </form>
      </div>
      <Chatbot />
    </div>
  );
};

export default Changepass;
