import axios from "axios";
import React, { useEffect, useState } from "react";
import NavbarPage from "../components/navnew";
import Axios from "axios";
import "./post.css";
import { Link, useHistory } from "react-router-dom";
import Chatbot from "../components/chatbot";
import { MDBBtn } from "mdbreact";
import {
  auth,
  googleProvider,
  facebookProvider,
  firestore,
} from "../Frontfirebase";
const Post = () => {
  const [data, Setdata] = useState();
  const [show, Setshow] = useState();
  const history = useHistory();

  const ok = async () => {
    const showdata = await firestore
      .collection("Post")
      .onSnapshot((querySnapshot) => {
        let item = [];
        querySnapshot.forEach((doc) => {
          item.push(doc.data());
          console.log(item);
        });
        Setshow(item);
      });
  };

  useEffect(() => {
    ok();
  }, []);

  const deleted = async (uid) => {
    const postdelete = await Axios.post(
      `http://localhost:7000/post/delete/${uid}`
    );
    console.log(postdelete.data);
  };

  return (
    <div>
      <NavbarPage />
      <h1 className="h1-post">โพสต์ทั้งหมด</h1>
      <div className="container-post1">
        <div className="row mypostcommentrow">
          <div className="column1 mypostcommentrow1">
            <div className="mypost-comment-img1">
              <img className="img-circle1" src="/img/logoLogin.png" />
              <div className="mypost-comment-name1">
                ค้นหาผ่านเว็บไซต์ของเราได้ที่นี่
              </div>
              <br />
              <div className="mypost-comment-comments1">
                <button className="buttonpost" type="submit" >
                  คลิก
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>  

      <div>
        <div className="container-post1">
          <div className="row-post1">
            <div className="column1-post">
              <img className="monkey" src="/img/logoLogin.png" />
            </div>

            <div className="column2-post">
              <div className="text1-index">ค้นหาผ่านเว็บไซต์ของเราได้ที่นี่</div>
              <button className="buttonpost" type="submit" >
                คลิก
              </button>
            </div>
          </div>
        </div>
      </div>
      {/* {show ? show .map(ok =>{
        return (
        <div>
        {ok.name} <br></br>
        {ok.accountnumber} <br></br>
        {ok.nameproduct} <br></br>
        {ok.money} <br></br>
        {ok.datetime} <br></br>
        <Link to={`/post/edit/${ok.uid}`}> Edit </Link>
        <button onClick={() =>  deleted(ok.uid)}> x </button> 
        </div>
      )
      }) : null} */}

      <Chatbot />
    </div>
  );
};

export default Post;
