import React, {useContext,useState,useEffect} from 'react'
import {
    auth,
    googleProvider,
    facebookProvider,
    firestore
  } from "../Frontfirebase.js";
import usercontext from "../context/usercontext"

export default function Mypost() {
    const [all,Setall] = useState()
    let { user , setUser} = useContext(usercontext)

   
 const ok = async () => {
    
     try{
         let userid = user.uid
         
    let mypost = await firestore.collection("Post").where("useruid" , "==" , userid).onSnapshot(snapshot =>{
        let allmypost = []
        snapshot.forEach(doc =>{
            allmypost.push(doc.data())
            Setall(allmypost)
        })
      
        console.log(all)
        
       
    })
   
    console.log(all)

 }catch(err){
     console.log("ff")
 }

 }
 

 useEffect(() => {
     ok()
 }, [user])





    return (
        <div>
            
            <h1> Hello </h1>
            {all ? all.map(res=>{
                return (
                    <div>
                        {res.name}
                    </div>
                )
            }) : null}
        </div>
    )
}
