import axios from "axios";
import React, { useEffect, useState } from "react";
import NavbarPage from "../components/navnew";
import Axios from "axios";
import "./post.css";
import { Link, useHistory } from "react-router-dom";
import Chatbot from "../components/chatbot";
import Commentitem from "../components/commentitem";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import {
  auth,
  googleProvider,
  facebookProvider,
  firestore,
} from "../Frontfirebase";
const Post = () => {
  const [data, Setdata] = useState();
  const [show, Setshow] = useState();
  const history = useHistory();

  const [isActive, setIsActive] = useState(false);
  const onClick = () => setIsActive(!isActive);

  const [Democomments, setDemocomments] = useState([
    { commment: "ไอนี้อีกแล้วหรอ น่าโดนจริงๆ อย่าให้เจอตัวบอกก่อน" },
    {
      commment: "โดนโกงไป5000 เจ็บใจจริงๆ TT ถ้าเจอจะซัดหน้าให้หมอบไปเลย55555",
    },
  ]);

  const ok = async () => {
    const showdata = await firestore
      .collection("Post")
      .onSnapshot((querySnapshot) => {
        let item = [];
        querySnapshot.forEach((doc) => {
          item.push(doc.data());
          console.log(item);
        });
        Setshow(item);
      });
  };

  useEffect(() => {
    ok();
  }, []);

  const deleted = async (uid) => {
    const postdelete = await Axios.post(
      `http://localhost:7000/post/delete/${uid}`
    );
    console.log(postdelete.data);
  };

  return (
    <div>
      <NavbarPage />
      <div className="container-bigpost">
        <div className="row postbigrow">
          <div className="column-post-left"></div>
          <div className="column-post-right"></div>
        </div>
        {/* {show ? show .map(ok =>{
        return (
        <div>
        {ok.name} <br></br>
        {ok.accountnumber} <br></br>
        {ok.nameproduct} <br></br>
        {ok.money} <br></br>
        {ok.datetime} <br></br>
        <Link to={`/post/edit/${ok.uid}`}> Edit </Link>
        <button onClick={() =>  deleted(ok.uid)}> x </button> 
        </div>
      )
      }) : null} */}
      </div>
      <Chatbot />
    </div>
  );
};

export default Post;
