                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridPrice"
                          >
                            <Form.Label className="text-mypost">
                              จำนวนเงิน (บาท){" "}
                              <span className="spanmypost">{ok.money}</span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group as={Col} controlId="formGridCategory">
                            <Form.Label className="text-mypost">
                              ธนาคาร{" "}
                              <span className="spanmypost">{ok.bank}</span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Row>
                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridDate"
                          >
                            <Form.Label className="text-mypost">
                              วันที่โดนโกง{" "}
                              <span className="spanmypost">{ok.datetime}</span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group as={Col} controlId="formGridSocial">
                            <Form.Label className="text-mypost">
                              ช่องทางที่โดนโกง{" "}
                              <span className="spanmypost">{ok.social}</span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Group controlId="exampleForm.ControlTextarea1">
                          <Form.Label className="text-mypost">
                            รายละเอียดเพิ่มเติม{" "}
                            <span className="spanmypost">{ok.other}</span>
                          </Form.Label>
                        </Form.Group>
                        <div className="img-holder-badslip">
                          {ok
                            ? ok.files.map((res) => {
                                return (
                                  <img
                                    className="img-bad"
                                    alt=""
                                    src={`/uploads/${res.filename}`}
                                    style={{ overflow: "hidden" }}
                                    onMouseOver={(e) =>
                                      (e.currentTarget.style = {
                                        transform: "scale(1.25)",
                                        overflow: "hidden",
                                      })
                                    }
                                    onMouseOut={(e) =>
                                      (e.currentTarget.style = {
                                        transform: "scale(1)",
                                        overflow: "hidden",
