const Createpost = () => {
  const [user1,Setuser1] = useState()
  let user = auth.currentUser;
  let history = useHistory
  const ok = async () =>{
  
    Setuser1(user)
  }


  useEffect(() => {
    ok()
}, [])
  return (
    <div className="allpage">
      <Navbar />
      <h1 className="h1-createpost">สร้างโพสต์</h1>
      {user1 ? <Formpost /> : history.push("/login")}
    </div>
  );
};

